	static {
		for (String row : graffitiTxt.split("\n")) {
			List<String> rowSplit = List.of(row.split("@"));
			graffiti.add(rowSplit);
		}
	}

	public static void createGraffiti(String msg) {
		for (List<String> rows : graffiti) {
			for (String character : List.of(msg.split(""))) {
				int indexOfChar = allChars.indexOf(character);
				System.out.print(rows.get(indexOfChar));
			}
			System.out.println();
		}
	}


